refentry.refsect1:
&reftitle.description;
mixedcurl_getinfo resourcech intopt0
Optiene información de la última transferencia.

  &reftitle.examples;



    Ejemplo curl_getinfo 


<?php
// Crear manipulador curl
$ch = curl_init('http://www.yahoo.com/');

// Ejecutar
curl_exec($ch);

// Comprueba si ocurrió un error
if(!curl_errno($ch))
{
 $info = curl_getinfo($ch);

 echo 'Tardó ' . $info['total_time'] . ' segundos en enviar la petición a ' . $info['url'];
}

// Cerrar manipulador
curl_close($ch);
?>
