   &reftitle.examples;



     Ejemplo de DBM


<?php

$dbm = dbmopen("lastseen", "w");
if (dbmexists($dbm, $id_usuario)) {
    $last_seen = dbmfetch($dbm, $id_usuario);
} else {
    dbminsert($dbm, $id_usuario, time());
}
hacer_algo();
dbmreplace($dbm, $id_usuario, time());
dbmclose($dbm);

?>







