  &reftitle.examples;



    Obtener todos los atributos de un nodo


<?php

include("example.inc");

if (!$dom = domxml_open_mem($xmlstr)) {
     echo "Error al analizar el documento\n";
     exit;
}

$root = $dom->document_element();
$attrs = $root->attributes();

echo 'Atributos de ' . $root->node_name() . "\n";
foreach ($attrs as $attribute) {
     echo ' - ' . $attribute->name . ' : ' . $attribute->value . "\n";
}

?>


