<?php

include 'ejemplo.inc';

$dom = domxml_open_mem($cadena_xml);

$capitulo = $dom->document_element();

// Veamos los elementos contenidos en capitulo
foreach($capitulo->child_nodes() as $nodo) {
  if ($nodo->node_type() == XML_ELEMENT_NODE) {
    echo $nodo->node_name() . "\n";
  }
}

?>


