  &reftitle.examples;


   
    Lee e imprime el contenido completo de un archivo CSV
    

<?php
$fila = 1;
if (($gestor = fopen("test.csv", "r")) !== FALSE) {
    while (($datos = fgetcsv($gestor, 1000, ",")) !== FALSE) {
        $número = count($datos);
        echo "<p> $número de campos en la línea $fila: <br /></p>\n";
        $fila++;
        for ($c=0; $c < $número; $c++) {
            echo $datos[$c] . "<br />\n";
        }
    }
    fclose($gestor);
}
?>
