&reftitle.description;
boolftp_cdup resourcesecuencia_ftp
Cambia al directorio padre.

  &reftitle.parameters;


   
    
     secuencia_ftp
     


       El identificador de enlace de la conexión FTP.

  &reftitle.examples;


   
    Ejemplo de ftp_cdup
    

<?php
// configurar conexi󮠢⴩ca
$id_con = ftp_connect($servidor_ftp);

// iniciar sesi󮠣on nombre de usuario y contrase񡍊$resultado_login = ftp_login($id_con, $ftp_nombre_usuario, $ftp_contrasenya_usuario);

// cambiar el directorio actual a html
ftp_chdir($id_con, 'html');

echo ftp_pwd($id_con); // /html

// regresar al directorio padre
if (ftp_cdup($id_con)) {
  echo "cdup tuvo exito\n";
} else {
  echo "cdup no tuvo exito\n";
}

echo ftp_pwd($id_con); // /

ftp_close($id_con);
?>
