 &reftitle.examples;


  Ejemplo de uso.


    Ejemplo de uso de la clase Gender.


   Ejemplo de uso.


<?php

namespace Gender;

$gender = new Gender;


$nombre = "Milene";
$pais = Gender::FRANCE;

$result = $gender->get($nombre, $pais);

$data = $gender->country($pais);

switch($result) {
    case Gender::IS_FEMALE:
        printf("El nombre %s es femenino en %s\n", $nombre, $data['country']);
    break;


    case Gender::IS_MOSTLY_FEMALE:
        printf("El nombre %s es generalmente femenino en %s\n", $nombre, $data['country']);
    break;


    case Gender::IS_MALE:
        printf("El nombre %s es masculino en %s\n", $nombre, $data['country']);
    break;


    case Gender::IS_MOSTLY_MALE:
        printf("El nombre %s es generalmente masculino en %s\n", $nombre, $data['country']);
    break;


    case Gender::IS_UNISEX_NAME:
        printf("El nombre %s es unisex en %s\n", $nombre, $data['country']);
    break;


    case Gender::IS_A_COUPLE:
        printf("El nombre %s es tanto masculino como femenino en %s\n", $nombre, $data['country']);
    break;


    case Gender::NAME_NOT_FOUND:
        printf("El nombre %s no se ha encontrado para %s\n", $nombre, $data['country']);
    break;


    case Gender::ERROR_IN_NAME:
        echo "¡Hay un error en el nombre proporcionado!\n";
    break;

    default:
        echo "¡Ocurrió un error!\n";
    break;

}
