refentry.refsect1:
  &reftitle.description;
  
   resourceimagecreatefromgd
   stringfilename
  


   Crear una imagen nueva desde un archivo GD o una URL.
  
  &tip.fopen-wrapper;

  &reftitle.examples;


   
    Ejemplo de imagecreatefromgd
    

<?php
// Cargar la imagen gd
$im = @imagecreatefromgd('./prueba.gd');

// Comprobar si la imagen fue cargada
if(!is_resource($im))
{
     die('¡No se puede cargar la imagen gd!');
}

// Hacer operaciones con la imagen aquí

// Guardar la imagen
imagegd($im, './prueba_actualizada.gd');
imagedestroy($im);
?>
