refentry.refsect1:
  &reftitle.description;
  
   resourceimagecreatefromgd2
   stringfilename
  


   Crea una imagen nueva desde un archivo GD2 o una URL.
  
  &tip.fopen-wrapper;

  &reftitle.parameters;


   
    
     filename
     


       Ruta a la imagen GD2.

  &reftitle.examples;


   
    Ejemplo de imagecreatefromgd2
    

<?php
// Cargar la imagen gd2
$im = imagecreatefromgd2('./prueba.gd2');

// Aplicar un efecto sobre la imagen, en este
// caso poner en negativo la imagen si PHP5+
if(function_exists('imagefilter'))
{
    imagefilter($im, IMG_FILTER_NEGATE);
}

// Guardar la imagen
imagegd2($im, './prueba_actualizada.gd2');
imagedestroy($im);
?>
