refentry.refsect1:
  &reftitle.description;
  
   resourceimagecreatefromjpeg
   stringfilename
  


   imagecreatefromjpeg devuelve un identificador de imagen
   que representa la imagen obtenida desde el nombre de archivo dado.
  
  &tip.fopen-wrapper;

  &reftitle.parameters;


   
    
     filename
     


       Ruta de la imagen JPEG.

  &reftitle.examples;
  
   Ejemplo de manejar un error durante la carga de un JPEG
   

<?php
function LoadJpeg($imagen)
{
    /* Intentar abrir */
    $im = @imagecreatefromjpeg($imagen);

    /* Ver si falló */
    if(!$im)
    {
        /* Crear una imagen en blanco */
        $im  = imagecreatetruecolor(150, 30);
        $bgc = imagecolorallocate($im, 255, 255, 255);
        $tc  = imagecolorallocate($im, 0, 0, 0);

        imagefilledrectangle($im, 0, 0, 150, 30, $bgc);

        /* Imprimir un mensaje de error */
        imagestring($im, 1, 5, 5, 'Error cargando ' . $imagen, $tc);
    }

    return $im;
}

header('Content-Type: image/jpeg');

$img = LoadJpeg('bogus.image');

imagejpeg($img);
imagedestroy($img);
?>

   
   &example.outputs.similar;
