refentry.refsect1:
  &reftitle.description;
  
   resourceimagecreatefrompng
   stringfilename
  


   imagecreatefrompng devuelve un identificador de imagen
   que representa la imagen obtenida desde el nombre de archivo dado.
  
  &tip.fopen-wrapper;

  &reftitle.parameters;


   
    
     filename
     


       Ruta de la imagen PNG.

  &reftitle.examples;
  
   Ejemplo de manejar un error durante la carga de un PNG
   

<?php
function CargarPNG($imagen)
{
    /* Intentar abrir */
    $im = @imagecreatefrompng($imagen);

    /* Ver si falló */
    if(!$im)
    {
        /* Crear una imagen en blanco */
        $im  = imagecreatetruecolor(150, 30);
        $fondo = imagecolorallocate($im, 255, 255, 255);
        $ct  = imagecolorallocate($im, 0, 0, 0);

        imagefilledrectangle($im, 0, 0, 150, 30, $fondo);

        /* Imprimir un mensaje de error */
        imagestring($im, 1, 5, 5, 'Error cargando ' . $imagen, $ct);
    }

    return $im;
}

header('Content-Type: image/png');

$img = CargarPNG('falso.image');

imagepng($img);
imagedestroy($img);
?>
