refentry.refsect1:
&reftitle.changelog;







     &Version;
     &Description;





     Desde 4.1.0

   Esta función puede ahora convertir números del tipo integer
   que eran muy grandes para encajar en dicho tipo, siendo en dicho caso
   devueltos los valores grandes como float.

  &reftitle.examples;



    Ejemplo de bindec


<?php
echo bindec('110011') . "\n";
echo bindec('000110011') . "\n";

echo bindec('111');
?>


    &example.outputs;


51
51
7







    bindec interpreta las entradas como enteros sin signo


<?php
/*
 * La lección de este ejemplo es el resultado 
 * más que el código en si mismo.
 */

$magnitude_lower = pow(2, (PHP_INT_SIZE * 8) - 2);
p($magnitude_lower - 1);
p($magnitude_lower, 'Observa el cambio?  Míralo la próxima vez...');

p(PHP_INT_MAX, 'PHP_INT_MAX');
p(~PHP_INT_MAX, 'interpretado para ser uno más que PHP_INT_MAX');

if (PHP_INT_SIZE == 4) {
    $note = 'interpretado para ser el mayor entero sin signo';
} else {
    $note = 'interpretado para ser el mayor entero sin signo
              (18446744073709551615) aunque sesgada por la precisión del float';
}
p(-1, $note);


function p($input, $note = '') {
    echo "input:        $input\n";

    $format = '%0' . (PHP_INT_SIZE * 8) . 'b';
    $bin = sprintf($format, $input);
    echo "binary:       $bin\n";

    ini_set('precision', 20);  // Para ser leido en bloques 64 bit.
    $dec = bindec($bin);
    echo 'bindec():     ' . $dec . "\n";

    if ($note) {
        echo "NOTA:         $note\n";
    }

    echo "\n";
}
?>


    &example.outputs.32bit;


input:        1073741823
binary:       00111111111111111111111111111111
bindec():     1073741823

input:        1073741824
binary:       01000000000000000000000000000000
bindec():     1073741824
NOTA:         Observa el cambio?  Míralo la próxima vez...

input:        2147483647
binary:       01111111111111111111111111111111
bindec():     2147483647
NOTA:         PHP_INT_MAX

input:        -2147483648
binary:       10000000000000000000000000000000
bindec():     2147483648
NOTA:         interpretado para ser uno más que PHP_INT_MAX

input:        -1
binary:       11111111111111111111111111111111
bindec():     4294967295
NOTA:         interpretado para ser el mayor entero sin signo


