  &reftitle.examples;



    Ejemplo de ob_get_flush


<?php
//usando output_buffering=On
print_r(ob_list_handlers());

//guardar búfer en un archivo
$bufer = ob_get_flush();
file_put_contents('bufer.txt', $bufer);

print_r(ob_list_handlers());
?>


