para.example:
      Ejemplo de ob_list_handlers


<?php
//usando output_buffering=On
print_r(ob_list_handlers());
ob_end_flush();

ob_start("ob_gzhandler");
print_r(ob_list_handlers());
ob_end_flush();

// funciones anonimas
ob_start(create_function('$cadena', 'return $cadena;'));
print_r(ob_list_handlers());
ob_end_flush();
?>


      &example.outputs;


