  &reftitle.examples;



    Ejemplo de preg_split: Obtener las partes de
    una cadena de búsqueda


<?php
// separar la frase por cualquier numero de comas o caracteres de espacio,
// incluyendo " ", \r, \t, \n y \f
$palabras_clave = preg_split("/[\s,]+/", "lenguaje hipertexto, programación");
?>







    Separar una cadena en sus caracteres


<?php
$cadena = 'cadena';
$caracteres = preg_split('//', $cadena, -1, PREG_SPLIT_NO_EMPTY);
print_r($caracteres);
?>







    Separar una cadena en coincidencias y sus
    desplazamientos


<?php
$cadena = 'programación lenguaje hipertexto';
$caracteres = preg_split('/ /', $cadena, -1, PREG_SPLIT_OFFSET_CAPTURE);
print_r($caracteres);
?>


    &example.outputs;


Array
(
    [0] => Array
        (
            [0] => programación
            [1] => 0
        )

    [1] => Array
        (
            [0] => lenguaje
            [1] => 13
        )

    [2] => Array
        (
            [0] => hipertexto
            [1] => 22
        )

)
