   &reftitle.interfacesynopsis;



    SeekableIterator




      SeekableIterator



      extends
      Iterator




    &Methods;


    &InheritedMethods;









    Uso básico


     Este ejemplo muestra cómo crear un SeekableIterator personalizado, 
     busca una posición y maneja una posición inválida.



<?php
class MySeekableIterator implements SeekableIterator {

    private $position = 0;

    private $array = array(
        "primer elemento",
        "segundo elemento",
        "tercer elemento",
        "cuarto elemento"
    );

    /* Métodos requeridos para la interfaz SeekableIterator */

    public function seek($position) {
      if (!isset($this->array[$position]) {
          throw new OutOfBoundsException("Posición seek inválida ($position)");
      }

      $this->position = $position;
    }

    /* Métodos requeridos para la interfaz Iterador */

    public function rewind() {
        $this->position = 0;
    }

    public function current() {
        return $this->array[$this->position];
    }

    public function key() {
        return $this->position;
    }

    public function next() {
        ++$this->position;
    }

    public function valid() {
        return isset($this->array[$this->position]);
    }
}

try {

    $it = new MySeekableIterator;
    echo $it->current(), "\n";

    $it->seek(2);
    echo $it->current(), "\n";

    $it->seek(1);
    echo $it->current(), "\n";

    $it->seek(10);

} catch (OutOfBoundsException $e) {
    echo $e->getMessage();
}
?>


