  &reftitle.parameters;




     manejador_resultado



       El identificador del resultado de SQLite. Este parámetro no es obligatorio cuando 
	   se emplea el método orientado a objetos.




     decodificar_binario

      &sqlite.decode-bin;

  &reftitle.examples;



    Ejemplo de sqlite_fetch_single


<?php
if ($manejador_bd = sqlite_open('mysqlitedb', 0666, $error_sqlite)) {

    $consulta = "SELECT id FROM usuarios WHERE id = 42";
    $resultado = sqlite_query($manejador_bd, $consulta);

    if (sqlite_num_rows($resultado) > 0) {
        echo sqlite_fetch_single($resultado); // 42
    }

    sqlite_close($manejador_bd);
}
?>
