  &reftitle.examples;



    Ejemplo básico de SwishResults->seekResult


<?php

try {

    $swish = new Swish("index.swish-e");
    $search = $swish->prepare();

    $results = $search->execute("lost");

    var_dump($results->seekResult(0)); //este será satifactorio
    var_dump($results->seekResult(100)); //este producirá un error

} catch (SwishException $e) {
    echo "Error: ", $e->getMessage(), "\n";
}

?>


