    Consulta-Delete


<?php
/* conectarse con la base de datos */
sybase_connect('SYBASE', '', '') or
die("No pudo conectarse");
sybase_select_db("bd");

sybase_query("DELETE FROM alguna_tabla WHERE id < 10");
printf("Registros eliminados: %d\n", sybase_affected_rows());
?>


