refentry.refsect1:
  &reftitle.examples;

   Usar un manejador de errores propio como un método


<?php

class mi_procesador_xslt {

    var $_xh; // nuestro procesador de XSLT

    function mi_procesador_xslt()
    {
        $this->_xh = xslt_create();

        // Hacer que el objeto $this resuelva la devolución de llamada
        xslt_set_object($this->_xh, $this);

        // Vamos a manejar los errores
        xslt_set_error_handler($this->_xh, "mi_manejador_errores_xslt");
    }

    function mi_manejador_errores_xslt($handler, $errno, $level, $info)
    {
        // por ahora, vamos a ver sólo los argumentos
        var_dump(func_get_args());
    }
}

?>
